# First half
def first_half():
    floor = 0

    for bracket in INPUT:
        
        if bracket == "(":
            floor += 1
        else:
            floor -= 1
            
    print(floor)

    
def second_half():
    floor = 0
    pos = 0

    for bracket in INPUT:
        pos += 1
        
        if bracket == "(":
            floor += 1
        else:
            floor -= 1
        
        if floor == -1:
            print(pos)
            break
    
